mapper.select:
select

from INPBILL.INP_BILL_DETAIL
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
and VISIT_ID = #{visitId,jdbcType=NUMERIC}
and ITEM_NO = #{itemNo,jdbcType=NUMERIC}

mapper.delete:
delete
from INPBILL.INP_BILL_DETAIL
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and ITEM_NO = #{itemNo,jdbcType=NUMERIC}
