mapper.select:
select 

from LAB_RESULT
where TEST_NO = #{testNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=NUMERIC}
  and PRINT_ORDER = #{printOrder,jdbcType=NUMERIC}

mapper.delete:
delete from LAB_RESULT
where TEST_NO = #{testNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=NUMERIC}
  and PRINT_ORDER = #{printOrder,jdbcType=NUMERIC}
