mapper.select:
select 

from DIAGNOSTIC_CATEGORY
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and DIAGNOSIS_TYPE = #{diagnosisType,jdbcType=VARCHAR}
  and DIAGNOSIS_NO = #{diagnosisNo,jdbcType=NUMERIC}

mapper.delete:
delete from DIAGNOSTIC_CATEGORY
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and DIAGNOSIS_TYPE = #{diagnosisType,jdbcType=VARCHAR}
  and DIAGNOSIS_NO = #{diagnosisNo,jdbcType=NUMERIC}
  and DIAGNOSIS_CODE = #{diagnosisCode,jdbcType=VARCHAR}

mapper.update:
update DIAGNOSTIC_CATEGORY


    CODE_NO = #{codeNo,jdbcType=NUMERIC},


    INSURANCE_ICD10_CODE = #{insuranceIcd10Code,jdbcType=VARCHAR},


    INSURANCE_ICD10_CODE_PLUS = #{insuranceIcd10CodePlus,jdbcType=VARCHAR},


    SH_ICD10_CODE = #{shIcd10Code,jdbcType=VARCHAR},


    SH_ICD10_CODE_PLUS = #{shIcd10CodePlus,jdbcType=VARCHAR},


    CLINIC_ICD10_CODE = #{clinicIcd10Code,jdbcType=VARCHAR},


    CLINIC_ICD10_CODE_PLUS = #{clinicIcd10CodePlus,jdbcType=VARCHAR},


    ICD10_CODE = #{icd10Code,jdbcType=VARCHAR},


    ICD10_CODE_PLUS = #{icd10CodePlus,jdbcType=VARCHAR},


where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and DIAGNOSIS_TYPE = #{diagnosisType,jdbcType=VARCHAR}
  and DIAGNOSIS_NO = #{diagnosisNo,jdbcType=NUMERIC}
  and DIAGNOSIS_CODE = #{diagnosisCode,jdbcType=VARCHAR}

update DIAGNOSTIC_CATEGORY
set CODE_NO = #{codeNo,jdbcType=NUMERIC},
  INSURANCE_ICD10_CODE = #{insuranceIcd10Code,jdbcType=VARCHAR},
  INSURANCE_ICD10_CODE_PLUS = #{insuranceIcd10CodePlus,jdbcType=VARCHAR},
  SH_ICD10_CODE = #{shIcd10Code,jdbcType=VARCHAR},
  SH_ICD10_CODE_PLUS = #{shIcd10CodePlus,jdbcType=VARCHAR},
  CLINIC_ICD10_CODE = #{clinicIcd10Code,jdbcType=VARCHAR},
  CLINIC_ICD10_CODE_PLUS = #{clinicIcd10CodePlus,jdbcType=VARCHAR},
  ICD10_CODE = #{icd10Code,jdbcType=VARCHAR},
  ICD10_CODE_PLUS = #{icd10CodePlus,jdbcType=VARCHAR}
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and DIAGNOSIS_TYPE = #{diagnosisType,jdbcType=VARCHAR}
  and DIAGNOSIS_NO = #{diagnosisNo,jdbcType=NUMERIC}
  and DIAGNOSIS_CODE = #{diagnosisCode,jdbcType=VARCHAR}
