mapper.select:
select 

from ORDADM.ORDERS
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and ORDER_NO = #{orderNo,jdbcType=NUMERIC}
  and ORDER_SUB_NO = #{orderSubNo,jdbcType=NUMERIC}

mapper.delete:
delete from ORDADM.ORDERS
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=NUMERIC}
  and ORDER_NO = #{orderNo,jdbcType=NUMERIC}
  and ORDER_SUB_NO = #{orderSubNo,jdbcType=NUMERIC}
